mapper.select:
		SELECT

        FROM
            product_discard a
        JOIN product b ON a.product_id = b.id
        LEFT JOIN product_detail c ON a.pro_detail_id = c.id
        WHERE a.del_flag = #{DEL_FLAG_NORMAL}

            AND a.dis_date = #{disDate}



                ORDER BY ${page.orderBy}


                ORDER BY dis_date
